import React from 'react'
import { useState } from 'react'
import {over} from 'stompjs'
import SockJS from 'sockjs-client'

var stompClient = null;
const ChatRoom = () => {
    const [publicChats, setPublicChats] = useState([]);
    const [privateChats, setPrivateChats] = useState(new Map());
    const [tab,setTab] = useState("CHATROOM")
    const [userData, setUserData] = useState({
        username:"",
        receivername:"",
        connected:false,
        message:""
    })

    const handleValue = (e) =>{
        const {value,name} = e.target;
        setUserData({...userData,[name]:value});
    }    

    const handleMessage = (e) =>{
      const {value} = e.target;
      setUserData({...userData,"message":value});
  }    

    const registerUser = () =>{
        let Sock = new SockJS("http://localhost:8080/ws");
        stompClient=over(Sock);
        stompClient.connect({},onConnected,onError);
    }

    const onConnected =() =>{
        setUserData({...userData,"connected":true});
        stompClient.subscribe('/chatroom/public',onPublicMessageReceivered);
        stompClient.subscribe('/user/'+userData.username+'/private',onPrivateMessageReceivered);
        userJoin();
    }
   const userJoin=()=>{
    
        let chatMessage={
          senderName:userData.username,
          status:'Join'
        };
        stompClient.send('/app/message',{},JSON.stringify(chatMessage));
        
      
    }
   
    const onError = (err)=>{
        console.log(err)
       }

   const onPublicMessageReceivered = (payload) => {
     let payloadData = JSON.parse(payload.body);
     switch(payloadData.status){
        case "Join":
            if(!privateChats.get(payloadData.senderName)){
                privateChats.set(payloadData.senderName,[]);
                setPrivateChats(new Map(privateChats));
        
            }
            break;
        case "Message":
            publicChats.push(payloadData)
            setPublicChats([...publicChats])
            break;    
     }
   }
   

   

   const onPrivateMessageReceivered = (payload) =>{
      let payloadData = JSON.parse(payload);
      if(privateChats.get(payloadData.senderName)){

        privateChats.get(payloadData.senderName).push(payloadData);
        setPrivateChats(new Map(privateChats));

      }else{
        let list  = [];
        list.push(payloadData);

        privateChats.set(payloadData.senderName,list);
        setPrivateChats(new Map(privateChats));
      }
}

const sendPublicMessage=()=>{
  if(stompClient){
    let chatMessage={
      senderName:userData.username,
      message:userData.message,
      status:'Message'
    };
    stompClient.send('/app/message',{},JSON.stringify(chatMessage));
    setUserData({...userData,"message":""});
  }
}

const sendPrivateMessage=()=>{
  if(stompClient){
    let chatMessage={
      senderName:userData.username,
      receivername:tab,
      message:userData.message,
      status:'Message'
    };
    if(userData.username !==tab){
      privateChats.set(tab).push(chatMessage);
      setPrivateChats(new Map(privateChats));
    }
    stompClient.send('/app/private-message',{},JSON.stringify(chatMessage));
    setUserData({...userData,"message":""});
  }
}

  return (
    <div className='container'>
        {userData.connected?
        <div className='chat-box'> 
          <div className='member-list'>
              <ul>
              <li onClick={()=>{setTab("CHATROOM")}} className={`member ${tab==="CHATROOM" && "active"}`}>Chatroom</li>
                  {[...privateChats.keys()].map((name,index)=>(
                      <li onClick={()=>{setTab(name)}} className={'member ${tab===name && "active"}'} key={index}>
                          {name}
                      </li>
                  ))}
              </ul>
            
          </div>        
         {tab === "CHATROOM" && <div className='chat-content'>
            <ul className='chat-meesages'>
          {[...privateChats.get(tab)].map((chat,index)=>(
                      <li className='message' key={index}>
                           {chat.senderName !== userData.username && <div className='avatar'></div>}
                           <div className='message'>{chat.message}</div>
                           {chat.senderName === userData.username && <div className='avatar self'></div>}
                          </li>
                  ))}
                  </ul>
           <div className='send-message'>
            <input type = 'text' className='input-message' placeholder='메세지 입력' value={userData.message}
                      name='message' onChange={handleValue}/>
            <button type='button' className='send-button' onClick={sendPublicMessage}>send</button>


            </div>

          </div>} 
          {tab !== "CHATROOM" && <div className='chat-content'>
          <ul className='chat-meesages'>
          {publicChats.map((chat,index)=>(
                      <li className='message' key={index}>
                           {chat.senderName !== userData.username && <div className='avatar'></div>}
                           <div className='message'>{chat.message}</div>
                           {chat.senderName === userData.username && <div className='avatar self'></div>}
                          </li>
                  ))}
                  </ul>
            <div className='send-message'>
            <input type = 'text' className='input-message' placeholder='${tab}에게 메세지 입력 ' value={userData.message}
                      name='message' onChange={handleValue}/>
            <button type='button' className='send-button' onClick={sendPrivateMessage}>send</button>

            </div>
                  
          </div>} 
        </div>
         :
        <div className='register'>
            <input
             id='user-name'
             name='username'
             placeholder='이름을 입력해주세요'
             value={userData.username}
             onChange={handleValue}
             />
             <button type='button' onClick={registerUser}>
             연결
             </button>
        </div> }
      
    </div>
  )
}

export default ChatRoom
